"815 :   private int addStubsForUndeclaredProperties(
816 :       Name n, String alias, Node parent, Node addAfter) {
817 :     Preconditions.checkArgument(NodeUtil.isStatementBlock(parent));
818 :     Preconditions.checkNotNull(addAfter);
819 :     int numStubs = 0;
820 :     if (n.props != null) {
821 :       for (Name p : n.props) {
822 :         if (p.needsToBeStubbed()) {
823 :           String propAlias = appendPropForAlias(alias, p.name);
824 :           Node nameNode = Node.newString(Token.NAME, propAlias);
825 :           Node newVar = new Node(Token.VAR, nameNode)
826 :               .copyInformationFromForTree(addAfter);
827 :           parent.addChildAfter(newVar, addAfter);
828 :           addAfter = newVar;
829 :           numStubs++;
830 :           compiler.reportCodeChange();
831 : 
832 :           // Determine if this is a constant var by checking the first
833 :           // reference to it. Don't check the declaration, as it might be null.
834 :           if (p.refs.get(0).node.getLastChild().getBooleanProp(
835 :                 Node.IS_CONSTANT_NAME)) {
836 :             nameNode.putBooleanProp(Node.IS_CONSTANT_NAME, true);
837 :           }
838 :         }
839 :       }
840 :     }
841 :     return numStubs;
842 :   }"

