"258 :   private Node tryMinimizeIf(Node n) {
259 : 
260 :     Node parent = n.getParent();
261 : 
262 :     Node cond = n.getFirstChild();
263 : 
264 :     /* If the condition is a literal, we'll let other
265 :      * optimizations try to remove useless code.
266 :      */
267 :     if (NodeUtil.isLiteralValue(cond, true)) {
268 :       return n;
269 :     }
270 : 
271 :     Node thenBranch = cond.getNext();
272 :     Node elseBranch = thenBranch.getNext();
273 : 
274 :     if (elseBranch == null) {
275 :       if (isFoldableExpressBlock(thenBranch)) {
276 :         Node expr = getBlockExpression(thenBranch);
277 :         if (isPropertyAssignmentInExpression(expr)) {
278 :           // Keep opportunities for CollapseProperties such as
279 :           // a.longIdentifier || a.longIdentifier = ... -> var a = ...;
280 :           return n;
281 :         }
282 : 
283 :         if (cond.getType() == Token.NOT) {
284 :           // if(!x)bar(); -> x||bar();
285 :           if (isLowerPrecedenceInExpression(cond, OR_PRECEDENCE) &&
286 :               isLowerPrecedenceInExpression(expr.getFirstChild(),
287 :                   OR_PRECEDENCE)) {
288 :             // It's not okay to add two sets of parentheses.
289 :             return n;
290 :           }
291 : 
292 :           Node or = new Node(Token.OR, cond.removeFirstChild(),
293 :           expr.removeFirstChild()).copyInformationFrom(n);
294 :           Node newExpr = NodeUtil.newExpr(or);
295 :           parent.replaceChild(n, newExpr);
296 :           reportCodeChange();
297 : 
298 :           return newExpr;
299 :         }
300 : 
301 :         // if(x)foo(); -> x&&foo();
302 :         if (isLowerPrecedenceInExpression(cond, AND_PRECEDENCE) ||
303 :             isLowerPrecedenceInExpression(expr.getFirstChild(),
304 :                 AND_PRECEDENCE)) {
305 :           // One additional set of parentheses isn't worth it.
306 :           return n;
307 :         }
308 : 
309 :         n.removeChild(cond);
310 :         Node and = new Node(Token.AND, cond, expr.removeFirstChild())
311 :                        .copyInformationFrom(n);
312 :         Node newExpr = NodeUtil.newExpr(and);
313 :         parent.replaceChild(n, newExpr);
314 :         reportCodeChange();
315 : 
316 :         return newExpr;
317 :       }
318 : 
319 :       return n;
320 :     }
321 : 
322 :     /* TODO(dcc) This modifies the siblings of n, which is undesirable for a
323 :      * peephole optimization. This should probably get moved to another pass.
324 :      */
325 :     tryRemoveRepeatedStatements(n);
326 : 
327 :     // if(!x)foo();else bar(); -> if(x)bar();else foo();
328 :     // An additional set of curly braces isn't worth it.
329 :     if (cond.getType() == Token.NOT && !consumesDanglingElse(elseBranch)) {
330 :       n.replaceChild(cond, cond.removeFirstChild());
331 :       n.removeChild(thenBranch);
332 :       n.addChildToBack(thenBranch);
333 :       reportCodeChange();
334 :       return n;
335 :     }
336 : 
337 :     // if(x)return 1;else return 2; -> return x?1:2;
338 :     if (isReturnExpressBlock(thenBranch) && isReturnExpressBlock(elseBranch)) {
339 :       Node thenExpr = getBlockReturnExpression(thenBranch);
340 :       Node elseExpr = getBlockReturnExpression(elseBranch);
341 :       n.removeChild(cond);
342 :       thenExpr.detachFromParent();
343 :       elseExpr.detachFromParent();
344 : 
345 :       // note - we ignore any cases with "return;", technically this
346 :       // can be converted to "return undefined;" or some variant, but
347 :       // that does not help code size.
348 :       Node hookNode = new Node(Token.HOOK, cond, thenExpr, elseExpr)
349 :                           .copyInformationFrom(n);
350 :       Node returnNode = new Node(Token.RETURN, hookNode);
351 :       parent.replaceChild(n, returnNode);
352 :       reportCodeChange();
353 :       return returnNode;
354 :     }
355 : 
356 :     boolean thenBranchIsExpressionBlock = isFoldableExpressBlock(thenBranch);
357 :     boolean elseBranchIsExpressionBlock = isFoldableExpressBlock(elseBranch);
358 : 
359 :     if (thenBranchIsExpressionBlock && elseBranchIsExpressionBlock) {
360 :       Node thenOp = getBlockExpression(thenBranch).getFirstChild();
361 :       Node elseOp = getBlockExpression(elseBranch).getFirstChild();
362 :       if (thenOp.getType() == elseOp.getType()) {
363 :         // if(x)a=1;else a=2; -> a=x?1:2;
364 :         if (NodeUtil.isAssignmentOp(thenOp)) {
365 :           Node lhs = thenOp.getFirstChild();
366 :           if (areNodesEqualForInlining(lhs, elseOp.getFirstChild()) &&
367 :               // if LHS has side effects, don't proceed [since the optimization
368 :               // evaluates LHS before cond]
369 :               // NOTE - there are some circumstances where we can
370 :               // proceed even if there are side effects...
371 :               !mayEffectMutableState(lhs)) {
372 : 
373 :             n.removeChild(cond);
374 :             Node assignName = thenOp.removeFirstChild();
375 :             Node thenExpr = thenOp.removeFirstChild();
376 :             Node elseExpr = elseOp.getLastChild();
377 :             elseOp.removeChild(elseExpr);
378 : 
379 :             Node hookNode = new Node(Token.HOOK, cond, thenExpr, elseExpr)
380 :                                 .copyInformationFrom(n);
381 :             Node assign = new Node(thenOp.getType(), assignName, hookNode)
382 :                               .copyInformationFrom(thenOp);
383 :             Node expr = NodeUtil.newExpr(assign);
384 :             parent.replaceChild(n, expr);
385 :             reportCodeChange();
386 : 
387 :             return expr;
388 :           }
389 :         } else if (NodeUtil.isCall(thenOp)) {
390 :           // if(x)foo();else bar(); -> x?foo():bar()
391 :           n.removeChild(cond);
392 :           thenOp.detachFromParent();
393 :           elseOp.detachFromParent();
394 :           Node hookNode = new Node(Token.HOOK, cond, thenOp, elseOp)
395 :                               .copyInformationFrom(n);
396 :           Node expr = NodeUtil.newExpr(hookNode);
397 :           parent.replaceChild(n, expr);
398 :           reportCodeChange();
399 : 
400 :           return expr;
401 :         }
402 :       }
403 :       return n;
404 :     }
405 : 
406 :     boolean thenBranchIsVar = isVarBlock(thenBranch);
407 :     boolean elseBranchIsVar = isVarBlock(elseBranch);
408 : 
409 :     // if(x)var y=1;else y=2  ->  var y=x?1:2
410 :     if (thenBranchIsVar && elseBranchIsExpressionBlock &&
411 :         NodeUtil.isAssign(getBlockExpression(elseBranch).getFirstChild())) {
412 : 
413 :       Node var = getBlockVar(thenBranch);
414 :       Node elseAssign = getBlockExpression(elseBranch).getFirstChild();
415 : 
416 :       Node name1 = var.getFirstChild();
417 :       Node maybeName2 = elseAssign.getFirstChild();
418 : 
419 :       if (name1.hasChildren()
420 :           && maybeName2.getType() == Token.NAME
421 :           && name1.getString().equals(maybeName2.getString())) {
422 :         Node thenExpr = name1.removeChildren();
423 :         Node elseExpr = elseAssign.getLastChild().detachFromParent();
424 :         cond.detachFromParent();
425 :         Node hookNode = new Node(Token.HOOK, cond, thenExpr, elseExpr)
426 :                             .copyInformationFrom(n);
427 :         var.detachFromParent();
428 :         name1.addChildrenToBack(hookNode);
429 :         parent.replaceChild(n, var);
430 :         reportCodeChange();
431 :         return var;
432 :       }
433 : 
434 :     // if(x)y=1;else var y=2  ->  var y=x?1:2
435 :     } else if (elseBranchIsVar && thenBranchIsExpressionBlock &&
436 :         NodeUtil.isAssign(getBlockExpression(thenBranch).getFirstChild())) {
437 : 
438 :       Node var = getBlockVar(elseBranch);
439 :       Node thenAssign = getBlockExpression(thenBranch).getFirstChild();
440 : 
441 :       Node maybeName1 = thenAssign.getFirstChild();
442 :       Node name2 = var.getFirstChild();
443 : 
444 :       if (name2.hasChildren()
445 :           && maybeName1.getType() == Token.NAME
446 :           && maybeName1.getString().equals(name2.getString())) {
447 :         Node thenExpr = thenAssign.getLastChild().detachFromParent();
448 :         Node elseExpr = name2.removeChildren();
449 :         cond.detachFromParent();
450 :         Node hookNode = new Node(Token.HOOK, cond, thenExpr, elseExpr)
451 :                             .copyInformationFrom(n);
452 :         var.detachFromParent();
453 :         name2.addChildrenToBack(hookNode);
454 :         parent.replaceChild(n, var);
455 :         reportCodeChange();
456 : 
457 :         return var;
458 :       }
459 :     }
460 : 
461 :     return n;
462 :   }"

