"780 :   private void removeUnreferencedVars() {
781 :     CodingConvention convention = codingConvention;
782 : 
783 :     for (Iterator<Var> it = maybeUnreferenced.iterator(); it.hasNext(); ) {
784 :       Var var = it.next();
785 : 
786 :       // Remove calls to inheritance-defining functions where the unreferenced
787 :       // class is the subclass.
788 :       for (Node exprCallNode : inheritsCalls.get(var)) {
789 :         NodeUtil.removeChild(exprCallNode.getParent(), exprCallNode);
790 :         compiler.reportCodeChange();
791 :       }
792 : 
793 :       // Regardless of what happens to the original declaration,
794 :       // we need to remove all assigns, because they may contain references
795 :       // to other unreferenced variables.
796 :       removeAllAssigns(var);
797 : 
798 :       compiler.addToDebugLog("Unreferenced var: " + var.name);
799 :       Node nameNode = var.nameNode;
800 :       Node toRemove = nameNode.getParent();
801 :       Node parent = toRemove.getParent();
802 : 
803 :       Preconditions.checkState(
804 :           toRemove.isVar() ||
805 :           toRemove.isFunction() ||
806 :           toRemove.isParamList() &&
807 :           parent.isFunction(),
808 :           "We should only declare vars and functions and function args");
809 : 
810 :       if (toRemove.isParamList() &&
811 :           parent.isFunction()) {
812 :         // Don't remove function arguments here. That's a special case
813 :         // that's taken care of in removeUnreferencedFunctionArgs.
814 :       } else if (NodeUtil.isFunctionExpression(toRemove)) {
815 :         if (!preserveFunctionExpressionNames) {
816 :           toRemove.getFirstChild().setString("");
817 :           compiler.reportCodeChange();
818 :         }
819 :         // Don't remove bleeding functions.
820 :       } else if (parent != null &&
821 :           parent.isFor() &&
822 :           parent.getChildCount() < 4) {
823 :         // foreach iterations have 3 children. Leave them alone.
824 :       } else if (toRemove.isVar() &&
825 :           nameNode.hasChildren() &&
826 :           NodeUtil.mayHaveSideEffects(nameNode.getFirstChild())) {
827 :         // If this is a single var declaration, we can at least remove the
828 :         // declaration itself and just leave the value, e.g.,
829 :         // var a = foo(); => foo();
830 :         if (toRemove.getChildCount() == 1) {
831 :           parent.replaceChild(toRemove,
832 :               IR.exprResult(nameNode.removeFirstChild()));
833 :           compiler.reportCodeChange();
834 :         }
835 :       } else if (toRemove.isVar() &&
836 :           toRemove.getChildCount() > 1) {
837 :         // For var declarations with multiple names (i.e. var a, b, c),
838 :         // only remove the unreferenced name
839 :         toRemove.removeChild(nameNode);
840 :         compiler.reportCodeChange();
841 :       } else if (parent != null) {
842 :         NodeUtil.removeChild(parent, toRemove);
843 :         compiler.reportCodeChange();
844 :       }
845 :     }
846 :   }"

