"366 :     private void splitObject(Var v, Reference declaration,
367 :                              Reference init,
368 :                              ReferenceCollection referenceInfo) {
369 :       // First figure out the FULL set of possible keys, so that they
370 :       // can all be properly set as necessary.
371 :       Map<String, String> varmap = computeVarList(v, referenceInfo);
372 : 
373 :       Map<String, Node> initvals = Maps.newHashMap();
374 :       // Figure out the top-level of the var assign node. If it's a plain
375 :       // ASSIGN, then there's an EXPR_STATEMENT above it, if it's a
376 :       // VAR then it should be directly replaced.
377 :       Node vnode;
378 :       boolean defined = referenceInfo.isWellDefined() &&
379 :           init.getParent().getType() == Token.VAR;
380 :       if (defined) {
381 :         vnode = init.getParent();
382 :         fillInitialValues(init, initvals);
383 :       } else {
384 :         // TODO(user): More test / rewrite this part.
385 :         // Find the beginning of the function / script.
386 :         vnode = v.getScope().getRootNode().getLastChild().getFirstChild();
387 :       }
388 : 
389 :       for (Map.Entry<String, String> entry : varmap.entrySet()) {
390 :         Node val = initvals.get(entry.getKey());
391 :         Node varnode = NodeUtil.newVarNode(entry.getValue(), val);
392 :         if (val == null) {
393 :           // is this right?
394 :           varnode.copyInformationFromForTree(vnode);
395 :         } else {
396 :           blacklistVarReferencesInTree(val, v.scope);
397 :         }
398 :         vnode.getParent().addChildBefore(varnode, vnode);
399 :       }
400 : 
401 :       if (defined) {
402 :         vnode.getParent().removeChild(vnode);
403 :       }
404 : 
405 :       for (Reference ref : referenceInfo.references) {
406 :         // The init/decl have already been converted.
407 :         if (defined && ref == init) continue;
408 : 
409 :         if (ref.isLvalue()) {
410 :           // Assignments have to be handled specially, since they
411 :           // expand out into multiple assignments.
412 :           replaceAssignmentExpression(v, ref, varmap);
413 :         } else if (ref.getParent().getType() == Token.VAR) {
414 :           // The old variable declaration. It didn't have a
415 :           // value. Remove it entirely as it should now be unused.
416 :           ref.getGrandparent().removeChild(ref.getParent());
417 :         } else {
418 :           // Make sure that the reference is a GETPROP as we expect it to be.
419 :           Node getprop = ref.getParent();
420 :           Preconditions.checkState(getprop.getType() == Token.GETPROP);
421 : 
422 :           // The key being looked up in the original map.
423 :           String var = getprop.getChildAtIndex(1).getString();
424 : 
425 :           // If the variable hasn't already been declared, add an empty
426 :           // declaration near all the other declarations.
427 :           Preconditions.checkState(varmap.containsKey(var));
428 : 
429 :           // Replace the GETPROP node with a NAME.
430 :           Node replacement = Node.newString(Token.NAME, varmap.get(var));
431 :           replacement.copyInformationFrom(getprop);
432 :           ref.getGrandparent().replaceChild(ref.getParent(), replacement);
433 :         }
434 :       }
435 : 
436 :       compiler.reportCodeChange();
437 :     }"

"303 :     private void replaceAssignmentExpression(Var v, Reference ref,
304 :                                              Map<String, String> varmap) {
305 :       // Compute all of the assignments necessary
306 :       List<Node> nodes = Lists.newArrayList();
307 :       Node val = ref.getAssignedValue();
308 :       blacklistVarReferencesInTree(val, v.scope);
309 :       Preconditions.checkState(val.getType() == Token.OBJECTLIT);
310 :       Set<String> all = Sets.newLinkedHashSet(varmap.keySet());
311 :       for (Node key = val.getFirstChild(); key != null;
312 :            key = key.getNext()) {
313 :         String var = key.getString();
314 :         Node value = key.removeFirstChild();
315 :         // TODO(user): Copy type information.
316 :         nodes.add(
317 :           new Node(Token.ASSIGN,
318 :                    Node.newString(Token.NAME, varmap.get(var)), value));
319 :         all.remove(var);
320 :       }
321 : 
322 :       // TODO(user): Better source information.
323 :       for (String var : all) {
324 :         nodes.add(
325 :           new Node(Token.ASSIGN,
326 :                    Node.newString(Token.NAME, varmap.get(var)),
327 :                    NodeUtil.newUndefinedNode(null)));
328 :       }
329 : 
330 :       Node replacement;
331 :         // All assignments evaluate to true, so make sure that the
332 :         // expr statement evaluates to true in case it matters.
333 :         nodes.add(new Node(Token.TRUE));
334 : 
335 :         // Join these using COMMA.  A COMMA node must have 2 children, so we
336 :         // create a tree. In the tree the first child be the COMMA to match
337 :         // the parser, otherwise tree equality tests fail.
338 :         nodes = Lists.reverse(nodes);
339 :         replacement = new Node(Token.COMMA);
340 :         Node cur = replacement;
341 :         int i;
342 :         for (i = 0; i < nodes.size() - 2; i++) {
343 :           cur.addChildToFront(nodes.get(i));
344 :           Node t = new Node(Token.COMMA);
345 :           cur.addChildToFront(t);
346 :           cur = t;
347 :         }
348 :         cur.addChildToFront(nodes.get(i));
349 :         cur.addChildToFront(nodes.get(i + 1));
350 : 
351 :       Node replace = ref.getParent();
352 :       replacement.copyInformationFromForTree(replace);
353 : 
354 :       if (replace.getType() == Token.VAR) {
355 :         replace.getParent().replaceChild(
356 :             replace, NodeUtil.newExpr(replacement));
357 :       } else {
358 :         replace.getParent().replaceChild(replace, replacement);
359 :       }
360 :     }"

