"407 :   private void checkPropertyVisibility(NodeTraversal t,
408 :       Node getprop, Node parent) {
409 :     ObjectType objectType =
410 :         ObjectType.cast(dereference(getprop.getFirstChild().getJSType()));
411 :     String propertyName = getprop.getLastChild().getString();
412 : 
413 :     if (objectType != null) {
414 :       // Is this a normal property access, or are we trying to override
415 :       // an existing property?
416 :       boolean isOverride = t.inGlobalScope() &&
417 :           parent.getType() == Token.ASSIGN &&
418 :           parent.getFirstChild() == getprop;
419 : 
420 :       // Find the lowest property defined on a class with visibility
421 :       // information.
422 :       if (isOverride) {
423 :         objectType = objectType.getImplicitPrototype();
424 :       }
425 :       JSDocInfo docInfo = null;
426 :       for (; objectType != null;
427 :            objectType = objectType.getImplicitPrototype()) {
428 :         docInfo = objectType.getOwnPropertyJSDocInfo(propertyName);
429 :         if (docInfo != null &&
430 :             docInfo.getVisibility() != Visibility.INHERITED) {
431 :           break;
432 :         }
433 :       }
434 : 
435 :       if (objectType == null) {
436 :         // We couldn't find a visibility modifier; assume it's public.
437 :         return;
438 :       }
439 : 
440 :       boolean sameInput =
441 :           t.getInput().getName().equals(docInfo.getSourceName());
442 :       Visibility visibility = docInfo.getVisibility();
443 :       JSType ownerType = normalizeClassType(objectType);
444 :       if (isOverride) {
445 :         // Check an ASSIGN statement that's trying to override a property
446 :         // on a superclass.
447 :         JSDocInfo overridingInfo = parent.getJSDocInfo();
448 :         Visibility overridingVisibility = overridingInfo == null ?
449 :             Visibility.INHERITED : overridingInfo.getVisibility();
450 : 
451 :         // Check that (a) the property *can* be overridden, and
452 :         // (b) that the visibility of the override is the same as the
453 :         // visibility of the original property.
454 :         if (visibility == Visibility.PRIVATE && !sameInput) {
455 :           compiler.report(
456 :               t.makeError(getprop, PRIVATE_OVERRIDE,
457 :                   objectType.toString()));
458 :         } else if (overridingVisibility != Visibility.INHERITED &&
459 :             overridingVisibility != visibility) {
460 :           compiler.report(
461 :               t.makeError(getprop, VISIBILITY_MISMATCH,
462 :                   visibility.name(), objectType.toString(),
463 :                   overridingVisibility.name()));
464 :         }
465 :       } else {
466 :         if (sameInput) {
467 :           // private access is always allowed in the same file.
468 :           return;
469 :         } else if (visibility == Visibility.PRIVATE &&
470 :             (currentClass == null || ownerType.differsFrom(currentClass))) {
471 :           if (docInfo.isConstructor() &&
472 :               isValidPrivateConstructorAccess(parent)) {
473 :             return;
474 :           }
475 : 
476 :           // private access is not allowed outside the file from a different
477 :           // enclosing class.
478 :           compiler.report(
479 :               t.makeError(getprop,
480 :                   BAD_PRIVATE_PROPERTY_ACCESS,
481 :                   propertyName,
482 :                   validator.getReadableJSTypeName(
483 :                       getprop.getFirstChild(), true)));
484 :         } else if (visibility == Visibility.PROTECTED) {
485 :           // There are 3 types of legal accesses of a protected property:
486 :           // 1) Accesses in the same file
487 :           // 2) Overriding the property in a subclass
488 :           // 3) Accessing the property from inside a subclass
489 :           // The first two have already been checked for.
490 :           if (currentClass == null || !currentClass.isSubtype(ownerType)) {
491 :             compiler.report(
492 :                 t.makeError(getprop,  BAD_PROTECTED_PROPERTY_ACCESS,
493 :                     propertyName,
494 :                     validator.getReadableJSTypeName(
495 :                         getprop.getFirstChild(), true)));
496 :           }
497 :         }
498 :       }
499 :     }
500 :   }"

