"398 :   private Node inlineFunction(
399 :       Node callNode, Node fnNode, String fnName) {
400 :     Node parent = callNode.getParent();
401 :     Node grandParent = parent.getParent();
402 : 
403 :     // TODO(johnlenz): Consider storing the callSite classification in the
404 :     // reference object and passing it in here.
405 :     CallSiteType callSiteType = classifyCallSite(callNode);
406 :     Preconditions.checkArgument(callSiteType != CallSiteType.UNSUPPORTED);
407 : 
408 :     boolean isCallInLoop = NodeUtil.isWithinLoop(callNode);
409 : 
410 :     // Store the name for the result. This will be used to
411 :     // replace "return expr" statements with "resultName = expr"
412 :     // to replace
413 :     String resultName = null;
414 :     boolean needsDefaultReturnResult = true;
415 :     switch (callSiteType) {
416 :       case SIMPLE_ASSIGNMENT:
417 :         resultName = parent.getFirstChild().getString();
418 :         break;
419 : 
420 :       case VAR_DECL_SIMPLE_ASSIGNMENT:
421 :         resultName = parent.getString();
422 :         break;
423 : 
424 :       case SIMPLE_CALL:
425 :         resultName = null;  // "foo()" doesn't need a result.
426 :         needsDefaultReturnResult = false;
427 :         break;
428 : 
429 :       case EXPRESSION:
430 :         resultName = getUniqueResultName();
431 :         // The intermediary result has a default value of "undefined", so
432 :         // we only need to set the implicit return value if we are in a loop
433 :         // and the variable maybe reused.
434 :         needsDefaultReturnResult = isCallInLoop;
435 :         break;
436 : 
437 :       case DECOMPOSABLE_EXPRESSION:
438 :         throw new IllegalStateException(
439 :             "Decomposable expressions must decomposed before inlining.");
440 : 
441 :       default:
442 :         throw new IllegalStateException("Unexpected call site type.");
443 :     }
444 : 
445 :     FunctionToBlockMutator mutator = new FunctionToBlockMutator(
446 :         compiler, this.safeNameIdSupplier);
447 : 
448 :     Node newBlock = mutator.mutate(
449 :         fnName, fnNode, callNode, resultName,
450 :         needsDefaultReturnResult, isCallInLoop);
451 : 
452 :     // TODO(nicksantos): Create a common mutation function that
453 :     // can replace either a VAR name assignment, assignment expression or
454 :     // a EXPR_RESULT.
455 :     Node greatGrandParent = grandParent.getParent();
456 :     switch (callSiteType) {
457 :       case VAR_DECL_SIMPLE_ASSIGNMENT:
458 :         // Remove the call from the name node.
459 :         parent.removeChild(parent.getFirstChild());
460 :         Preconditions.checkState(parent.getFirstChild() == null);
461 :         // Add the call, after the VAR.
462 :         greatGrandParent.addChildAfter(newBlock, grandParent);
463 :         break;
464 : 
465 :       case SIMPLE_ASSIGNMENT:
466 :         // The assignment is now part of the inline function so
467 :         // replace it completely.
468 :         Preconditions.checkState(NodeUtil.isExpressionNode(grandParent));
469 :         greatGrandParent.replaceChild(grandParent, newBlock);
470 :         break;
471 : 
472 :       case SIMPLE_CALL:
473 :         // If nothing is looking at the result just replace the call.
474 :         Preconditions.checkState(NodeUtil.isExpressionNode(parent));
475 :         grandParent.replaceChild(parent, newBlock);
476 :         break;
477 : 
478 :       case EXPRESSION:
479 :         // TODO(johnlenz): Maybe change this so that movable and decomposable
480 :         // expressions are handled the same way: The call is moved and
481 :         // then handled by one the three basic cases, rather than
482 :         // introducing a new case.
483 :         Node injectionPoint = ExpressionDecomposer.findInjectionPoint(callNode);
484 :         Preconditions.checkNotNull(injectionPoint);
485 :         Node injectionPointParent = injectionPoint.getParent();
486 :         Preconditions.checkNotNull(injectionPointParent);
487 :         Preconditions.checkState(
488 :             NodeUtil.isStatementBlock(injectionPointParent));
489 : 
490 :         // Declare the intermediate result name.
491 :         newBlock.addChildrenToFront(
492 :             NodeUtil.newVarNode(resultName, null)
493 :                  .copyInformationFromForTree(callNode));
494 :         // Inline the function before the selected injection point (before
495 :         // the call).
496 :         injectionPointParent.addChildBefore(newBlock, injectionPoint);
497 :         // Replace the call site with a reference to the intermediate
498 :         // result name.
499 :         parent.replaceChild(callNode, Node.newString(Token.NAME, resultName));
500 :         break;
501 : 
502 :       default:
503 :         throw new IllegalStateException("Unexpected call site type.");
504 :     }
505 : 
506 :     return newBlock;
507 :   }"

