"350 :     public long getDateTimeMillis(int year, int monthOfYear, int dayOfMonth,
351 :                                   int hourOfDay, int minuteOfHour,
352 :                                   int secondOfMinute, int millisOfSecond)
353 :         throws IllegalArgumentException
354 :     {
355 :         Chronology base;
356 :         if ((base = getBase()) != null) {
357 :             return base.getDateTimeMillis
358 :                 (year, monthOfYear, dayOfMonth,
359 :                  hourOfDay, minuteOfHour, secondOfMinute, millisOfSecond);
360 :         }
361 : 
362 :         // Assume date is Gregorian.
363 :         long instant;
364 :             instant = iGregorianChronology.getDateTimeMillis
365 :                 (year, monthOfYear, dayOfMonth,
366 :                  hourOfDay, minuteOfHour, secondOfMinute, millisOfSecond);
367 :         if (instant < iCutoverMillis) {
368 :             // Maybe it's Julian.
369 :             instant = iJulianChronology.getDateTimeMillis
370 :                 (year, monthOfYear, dayOfMonth,
371 :                  hourOfDay, minuteOfHour, secondOfMinute, millisOfSecond);
372 :             if (instant >= iCutoverMillis) {
373 :                 // Okay, it's in the illegal cutover gap.
374 :                 throw new IllegalArgumentException("Specified date does not exist");
375 :             }
376 :         }
377 :         return instant;
378 :     }"

