"240 :     @Override
241 :     protected VectorialPointValuePair doOptimize()
242 :         throws FunctionEvaluationException, OptimizationException, IllegalArgumentException {
243 : 
244 :         // arrays shared with the other private methods
245 :         solvedCols  = Math.min(rows, cols);
246 :         diagR       = new double[cols];
247 :         jacNorm     = new double[cols];
248 :         beta        = new double[cols];
249 :         permutation = new int[cols];
250 :         lmDir       = new double[cols];
251 : 
252 :         // local point
253 :         double   delta   = 0;
254 :         double   xNorm   = 0;
255 :         double[] diag    = new double[cols];
256 :         double[] oldX    = new double[cols];
257 :         double[] oldRes  = new double[rows];
258 :         double[] work1   = new double[cols];
259 :         double[] work2   = new double[cols];
260 :         double[] work3   = new double[cols];
261 : 
262 :         // evaluate the function at the starting point and calculate its norm
263 :         updateResidualsAndCost();
264 : 
265 :         // outer loop
266 :         lmPar = 0;
267 :         boolean firstIteration = true;
268 :         VectorialPointValuePair current = new VectorialPointValuePair(point, objective);
269 :         while (true) {
270 :             incrementIterationsCounter();
271 : 
272 :             // compute the Q.R. decomposition of the jacobian matrix
273 :             VectorialPointValuePair previous = current;
274 :             updateJacobian();
275 :             qrDecomposition();
276 : 
277 :             // compute Qt.res
278 :             qTy(residuals);
279 :             // now we don't need Q anymore,
280 :             // so let jacobian contain the R matrix with its diagonal elements
281 :             for (int k = 0; k < solvedCols; ++k) {
282 :                 int pk = permutation[k];
283 :                 jacobian[k][pk] = diagR[pk];
284 :             }
285 : 
286 :             if (firstIteration) {
287 : 
288 :                 // scale the point according to the norms of the columns
289 :                 // of the initial jacobian
290 :                 xNorm = 0;
291 :                 for (int k = 0; k < cols; ++k) {
292 :                     double dk = jacNorm[k];
293 :                     if (dk == 0) {
294 :                         dk = 1.0;
295 :                     }
296 :                     double xk = dk * point[k];
297 :                     xNorm  += xk * xk;
298 :                     diag[k] = dk;
299 :                 }
300 :                 xNorm = Math.sqrt(xNorm);
301 : 
302 :                 // initialize the step bound delta
303 :                 delta = (xNorm == 0) ? initialStepBoundFactor : (initialStepBoundFactor * xNorm);
304 : 
305 :             }
306 : 
307 :             // check orthogonality between function vector and jacobian columns
308 :             double maxCosine = 0;
309 :             if (cost != 0) {
310 :                 for (int j = 0; j < solvedCols; ++j) {
311 :                     int    pj = permutation[j];
312 :                     double s  = jacNorm[pj];
313 :                     if (s != 0) {
314 :                         double sum = 0;
315 :                         for (int i = 0; i <= j; ++i) {
316 :                             sum += jacobian[i][pj] * residuals[i];
317 :                         }
318 :                         maxCosine = Math.max(maxCosine, Math.abs(sum) / (s * cost));
319 :                     }
320 :                 }
321 :             }
322 :             if (maxCosine <= orthoTolerance) {
323 :                 // convergence has been reached
324 :                 return current;
325 :             }
326 : 
327 :             // rescale if necessary
328 :             for (int j = 0; j < cols; ++j) {
329 :                 diag[j] = Math.max(diag[j], jacNorm[j]);
330 :             }
331 : 
332 :             // inner loop
333 :             for (double ratio = 0; ratio < 1.0e-4;) {
334 : 
335 :                 // save the state
336 :                 for (int j = 0; j < solvedCols; ++j) {
337 :                     int pj = permutation[j];
338 :                     oldX[pj] = point[pj];
339 :                 }
340 :                 double previousCost = cost;
341 :                 double[] tmpVec = residuals;
342 :                 residuals = oldRes;
343 :                 oldRes    = tmpVec;
344 : 
345 :                 // determine the Levenberg-Marquardt parameter
346 :                 determineLMParameter(oldRes, delta, diag, work1, work2, work3);
347 : 
348 :                 // compute the new point and the norm of the evolution direction
349 :                 double lmNorm = 0;
350 :                 for (int j = 0; j < solvedCols; ++j) {
351 :                     int pj = permutation[j];
352 :                     lmDir[pj] = -lmDir[pj];
353 :                     point[pj] = oldX[pj] + lmDir[pj];
354 :                     double s = diag[pj] * lmDir[pj];
355 :                     lmNorm  += s * s;
356 :                 }
357 :                 lmNorm = Math.sqrt(lmNorm);
358 :                 // on the first iteration, adjust the initial step bound.
359 :                 if (firstIteration) {
360 :                     delta = Math.min(delta, lmNorm);
361 :                 }
362 : 
363 :                 // evaluate the function at x + p and calculate its norm
364 :                 updateResidualsAndCost();
365 :                 current = new VectorialPointValuePair(point, objective);
366 : 
367 :                 // compute the scaled actual reduction
368 :                 double actRed = -1.0;
369 :                 if (0.1 * cost < previousCost) {
370 :                     double r = cost / previousCost;
371 :                     actRed = 1.0 - r * r;
372 :                 }
373 : 
374 :                 // compute the scaled predicted reduction
375 :                 // and the scaled directional derivative
376 :                 for (int j = 0; j < solvedCols; ++j) {
377 :                     int pj = permutation[j];
378 :                     double dirJ = lmDir[pj];
379 :                     work1[j] = 0;
380 :                     for (int i = 0; i <= j; ++i) {
381 :                         work1[i] += jacobian[i][pj] * dirJ;
382 :                     }
383 :                 }
384 :                 double coeff1 = 0;
385 :                 for (int j = 0; j < solvedCols; ++j) {
386 :                     coeff1 += work1[j] * work1[j];
387 :                 }
388 :                 double pc2 = previousCost * previousCost;
389 :                 coeff1 = coeff1 / pc2;
390 :                 double coeff2 = lmPar * lmNorm * lmNorm / pc2;
391 :                 double preRed = coeff1 + 2 * coeff2;
392 :                 double dirDer = -(coeff1 + coeff2);
393 : 
394 :                 // ratio of the actual to the predicted reduction
395 :                 ratio = (preRed == 0) ? 0 : (actRed / preRed);
396 : 
397 :                 // update the step bound
398 :                 if (ratio <= 0.25) {
399 :                     double tmp =
400 :                         (actRed < 0) ? (0.5 * dirDer / (dirDer + 0.5 * actRed)) : 0.5;
401 :                         if ((0.1 * cost >= previousCost) || (tmp < 0.1)) {
402 :                             tmp = 0.1;
403 :                         }
404 :                         delta = tmp * Math.min(delta, 10.0 * lmNorm);
405 :                         lmPar /= tmp;
406 :                 } else if ((lmPar == 0) || (ratio >= 0.75)) {
407 :                     delta = 2 * lmNorm;
408 :                     lmPar *= 0.5;
409 :                 }
410 : 
411 :                 // test for successful iteration.
412 :                 if (ratio >= 1.0e-4) {
413 :                     // successful iteration, update the norm
414 :                     firstIteration = false;
415 :                     xNorm = 0;
416 :                     for (int k = 0; k < cols; ++k) {
417 :                         double xK = diag[k] * point[k];
418 :                         xNorm    += xK * xK;
419 :                     }
420 :                     xNorm = Math.sqrt(xNorm);
421 : 
422 :                     // tests for convergence.
423 :                     // we use the vectorial convergence checker
424 :                 } else {
425 :                     // failed iteration, reset the previous values
426 :                     cost = previousCost;
427 :                     for (int j = 0; j < solvedCols; ++j) {
428 :                         int pj = permutation[j];
429 :                         point[pj] = oldX[pj];
430 :                     }
431 :                     tmpVec    = residuals;
432 :                     residuals = oldRes;
433 :                     oldRes    = tmpVec;
434 :                 }
435 :                 if (checker==null) {
436 :                 	if (((Math.abs(actRed) <= costRelativeTolerance) &&
437 :                         (preRed <= costRelativeTolerance) &&
438 :                         (ratio <= 2.0)) ||
439 :                        (delta <= parRelativeTolerance * xNorm)) {
440 :                        return current;
441 :                    }
442 :                 } else {
443 :                     if (checker.converged(getIterations(), previous, current)) {
444 :                         return current;
445 :                     }
446 :                 }
447 :                 // tests for termination and stringent tolerances
448 :                 // (2.2204e-16 is the machine epsilon for IEEE754)
449 :                 if ((Math.abs(actRed) <= 2.2204e-16) && (preRed <= 2.2204e-16) && (ratio <= 2.0)) {
450 :                     throw new OptimizationException(LocalizedFormats.TOO_SMALL_COST_RELATIVE_TOLERANCE,
451 :                             costRelativeTolerance);
452 :                 } else if (delta <= 2.2204e-16 * xNorm) {
453 :                     throw new OptimizationException(LocalizedFormats.TOO_SMALL_PARAMETERS_RELATIVE_TOLERANCE,
454 :                             parRelativeTolerance);
455 :                 } else if (maxCosine <= 2.2204e-16)  {
456 :                     throw new OptimizationException(LocalizedFormats.TOO_SMALL_ORTHOGONALITY_TOLERANCE,
457 :                             orthoTolerance);
458 :                 }
459 : 
460 :             }
461 : 
462 :         }
463 : 
464 :     }"
