"106 :   void tryMinimizeExits(Node n, int exitType, String labelName) {
107 : 
108 :     // Just an 'exit'.
109 :     if (matchingExitNode(n, exitType, labelName)) {
110 :       NodeUtil.removeChild(n.getParent(), n);
111 :       compiler.reportCodeChange();
112 :       return;
113 :     }
114 : 
115 :     // Just an 'if'.
116 :     if (n.isIf()) {
117 :       Node ifBlock = n.getFirstChild().getNext();
118 :       tryMinimizeExits(ifBlock, exitType, labelName);
119 :       Node elseBlock = ifBlock.getNext();
120 :       if (elseBlock != null) {
121 :         tryMinimizeExits(elseBlock, exitType, labelName);
122 :       }
123 :       return;
124 :     }
125 : 
126 :     // Just a 'try/catch/finally'.
127 :     if (n.isTry()) {
128 :       Node tryBlock = n.getFirstChild();
129 :       tryMinimizeExits(tryBlock, exitType, labelName);
130 :       Node allCatchNodes = NodeUtil.getCatchBlock(n);
131 :       if (NodeUtil.hasCatchHandler(allCatchNodes)) {
132 :         Preconditions.checkState(allCatchNodes.hasOneChild());
133 :         Node catchNode = allCatchNodes.getFirstChild();
134 :         Node catchCodeBlock = catchNode.getLastChild();
135 :         tryMinimizeExits(catchCodeBlock, exitType, labelName);
136 :       }
137 :       /* Don't try to minimize the exits of finally blocks, as this
138 :        * can cause problems if it changes the completion type of the finally
139 :        * block. See ECMA 262 Sections 8.9 & 12.14
140 :        */
141 :       if (NodeUtil.hasFinally(n)) {
142 :         Node finallyBlock = n.getLastChild();
143 :         tryMinimizeExits(finallyBlock, exitType, labelName);
144 :       }
145 :     }
146 : 
147 :     // Just a 'label'.
148 :     if (n.isLabel()) {
149 :       Node labelBlock = n.getLastChild();
150 :       tryMinimizeExits(labelBlock, exitType, labelName);
151 :     }
152 : 
153 :     // TODO(johnlenz): The last case of SWITCH statement?
154 : 
155 :     // The rest assumes a block with at least one child, bail on anything else.
156 :     if (!n.isBlock() || n.getLastChild() == null) {
157 :       return;
158 :     }
159 : 
160 :     // Multiple if-exits can be converted in a single pass.
161 :     // Convert "if (blah) break;  if (blah2) break; other_stmt;" to
162 :     // become "if (blah); else { if (blah2); else { other_stmt; } }"
163 :     // which will get converted to "if (!blah && !blah2) { other_stmt; }".
164 :     for (Node c : n.children()) {
165 : 
166 :       // An 'if' block to process below.
167 :       if (c.isIf()) {
168 :         Node ifTree = c;
169 :         Node trueBlock, falseBlock;
170 : 
171 :         // First, the true condition block.
172 :         trueBlock = ifTree.getFirstChild().getNext();
173 :         falseBlock = trueBlock.getNext();
174 :         tryMinimizeIfBlockExits(trueBlock, falseBlock,
175 :             ifTree, exitType, labelName);
176 : 
177 :         // Now the else block.
178 :         // The if blocks may have changed, get them again.
179 :         trueBlock = ifTree.getFirstChild().getNext();
180 :         falseBlock = trueBlock.getNext();
181 :         if (falseBlock != null) {
182 :           tryMinimizeIfBlockExits(falseBlock, trueBlock,
183 :               ifTree, exitType, labelName);
184 :         }
185 :       }
186 : 
187 :       if (c == n.getLastChild()) {
188 :         break;
189 :       }
190 :     }
191 : 
192 :     // Now try to minimize the exits of the last child, if it is removed
193 :     // look at what has become the last child.
194 :     for (Node c = n.getLastChild(); c != null; c = n.getLastChild()) {
195 :       tryMinimizeExits(c, exitType, labelName);
196 :       // If the node is still the last child, we are done.
197 :       if (c == n.getLastChild()) {
198 :         break;
199 :       }
200 :     }
201 :   }"

"276 :   private static boolean matchingExitNode(Node n, int type, String labelName) {
277 :     if (n.getType() == type) {
278 :       if (type == Token.RETURN) {
279 :         // only returns without expressions.
280 :         return !n.hasChildren();
281 :       } else {
282 :         if (labelName == null) {
283 :           return !n.hasChildren();
284 :         } else {
285 :           return n.hasChildren()
286 :             && labelName.equals(n.getFirstChild().getString());
287 :         }
288 :       }
289 :     }
290 :     return false;
291 :   }"

