"269 :   private void testClosureFunction(String function, JSType type,
270 :       JSType trueType, JSType falseType) {
271 :     // function(a) where a : type
272 :     Node n = compiler.parseTestCode("var a; " + function + "(a)");
273 :     Node call = n.getLastChild().getLastChild();
274 :     Node name = call.getLastChild();
275 : 
276 :     Scope scope = new SyntacticScopeCreator(compiler).createScope(n, null);
277 :     FlowScope flowScope = LinkedFlowScope.createEntryLattice(scope);
278 : 
279 :     assertEquals(Token.CALL, call.getType());
280 :     assertEquals(Token.NAME, name.getType());
281 : 
282 :     GoogleCodingConvention convention = new GoogleCodingConvention();
283 :     flowScope.inferSlotType("a", type);
284 :     ClosureReverseAbstractInterpreter rai =
285 :         new ClosureReverseAbstractInterpreter(convention, registry);
286 : 
287 :     // trueScope
288 :     Asserts.assertTypeEquals(
289 :         trueType,
290 :         rai.getPreciserScopeKnowingConditionOutcome(call, flowScope, true)
291 :         .getSlot("a").getType());
292 : 
293 :     // falseScope
294 :     Asserts.assertTypeEquals(
295 :         falseType,
296 :         rai.getPreciserScopeKnowingConditionOutcome(call, flowScope, false)
297 :         .getSlot("a").getType());
298 :   }"

