"209 :   private void applyCollapses() {
210 :     for (Collapse collapse : collapses) {
211 : 
212 :       Node var = new Node(Token.VAR);
213 :       var.copyInformationFrom(collapse.startNode);
214 :       collapse.parent.addChildBefore(var, collapse.startNode);
215 : 
216 :       boolean redeclaration = false;
217 :       for (Node n = collapse.startNode; n != collapse.endNode;) {
218 :         Node next = n.getNext();
219 : 
220 :         Preconditions.checkState(var.getNext() == n);
221 :         collapse.parent.removeChildAfter(var);
222 : 
223 :         if (n.isVar()) {
224 :           while(n.hasChildren()) {
225 :             var.addChildToBack(n.removeFirstChild());
226 :           }
227 :         } else {
228 :           Node assign = n.getFirstChild();
229 :           Node lhs = assign.getFirstChild();
230 :           Preconditions.checkState(lhs.isName());
231 :           Node rhs = assign.getLastChild();
232 :           lhs.addChildToBack(rhs.detachFromParent());
233 :           var.addChildToBack(lhs.detachFromParent());
234 :           redeclaration = true;
235 :         }
236 :         n = next;
237 :       }
238 : 
239 :       if (redeclaration) {
240 :         JSDocInfo info = new JSDocInfo();
241 :         info.addSuppression("duplicate");
242 :         var.setJSDocInfo(info);
243 :       }
244 :     }
245 :   }"

