"136 :     @Override
137 :     public void visit(NodeTraversal t, Node n, Node parent) {
138 :       if (n.isVar()) {
139 :         blacklistStubVars(t, n);
140 :       }
141 : 
142 :       // Only care about var nodes
143 :       if (!n.isVar() && !canBeRedeclared(n, t.getScope())) return;
144 : 
145 :       // If we've already looked at this node, skip it
146 :       if (nodesToCollapse.contains(n)) return;
147 : 
148 :       // Adjacent VAR children of an IF node are the if and else parts and can't
149 :       // be collapsed
150 :       if (parent.isIf()) return;
151 : 
152 :       Node varNode = n;
153 : 
154 :       boolean hasVar = n.isVar();
155 : 
156 :       // Find variable declarations that follow this one (if any)
157 :       n = n.getNext();
158 : 
159 :       boolean hasNodesToCollapse = false;
160 : 
161 :       while (n != null &&
162 :           (n.isVar() || canBeRedeclared(n, t.getScope()))) {
163 : 
164 :         if (n.isVar()) {
165 :           blacklistStubVars(t, n);
166 :           hasVar = true;
167 :         }
168 : 
169 :         nodesToCollapse.add(n);
170 :         hasNodesToCollapse = true;
171 : 
172 :         n = n.getNext();
173 :       }
174 : 
175 :       if (hasNodesToCollapse && hasVar) {
176 :         nodesToCollapse.add(varNode);
177 :         collapses.add(new Collapse(varNode, n, parent));
178 :       }
179 :     }"

