" 82 :   public boolean shouldTraverse(NodeTraversal t, Node n, Node parent) {
 83 : 
 84 :     if (n.getType() == Token.FUNCTION) {
 85 :       // Don't traverse functions that are constructors or have the @this
 86 :       // or @override annotation.
 87 :       JSDocInfo jsDoc = getFunctionJsDocInfo(n);
 88 :       if (jsDoc != null &&
 89 :           (jsDoc.isConstructor() ||
 90 :            jsDoc.isInterface() ||
 91 :            jsDoc.hasThisType() ||
 92 :            jsDoc.isOverride())) {
 93 :         return false;
 94 :       }
 95 : 
 96 :       // Don't traverse functions unless they would normally
 97 :       // be able to have a @this annotation associated with them. e.g.,
 98 :       // var a = function() { }; // or
 99 :       // function a() {} // or
100 :       // a.x = function() {}; // or
101 :       // var a = {x: function() {}};
102 :       int pType = parent.getType();
103 :       if (!(pType == Token.BLOCK ||
104 :             pType == Token.SCRIPT ||
105 :             pType == Token.NAME ||
106 :             pType == Token.ASSIGN ||
107 : 
108 :             // object literal keys
109 :             pType == Token.STRING ||
110 :             pType == Token.NUMBER)) {
111 :         return false;
112 :       }
113 : 
114 :       // Don't traverse functions that are getting lent to a prototype.
115 :     }
116 : 
117 :     if (parent != null && parent.getType() == Token.ASSIGN) {
118 :       Node lhs = parent.getFirstChild();
119 :       Node rhs = lhs.getNext();
120 : 
121 :       if (n == lhs) {
122 :         // Always traverse the left side of the assignment. To handle
123 :         // nested assignments properly (e.g., (a = this).property = c;),
124 :         // assignLhsChild should not be overridden.
125 :         if (assignLhsChild == null) {
126 :           assignLhsChild = lhs;
127 :         }
128 :       } else {
129 :         // Only traverse the right side if it's not an assignment to a prototype
130 :         // property or subproperty.
131 :         if (NodeUtil.isGet(lhs)) {
132 :           if (lhs.getType() == Token.GETPROP &&
133 :               lhs.getLastChild().getString().equals("prototype")) {
134 :             return false;
135 :           }
136 :           Node llhs = lhs.getFirstChild();
137 :           if (llhs.getType() == Token.GETPROP &&
138 :               llhs.getLastChild().getString().equals("prototype")) {
139 :             return false;
140 :           }
141 :         }
142 :       }
143 :     }
144 : 
145 :     return true;
146 :   }"

"157 :   private boolean shouldReportThis(Node n, Node parent) {
158 :     if (assignLhsChild != null) {
159 :       // Always report a THIS on the left side of an assign.
160 :       return true;
161 :     }
162 : 
163 :     // Also report a THIS with a property access.
164 :     return parent != null && NodeUtil.isGet(parent);
165 :   }"

"148 :   public void visit(NodeTraversal t, Node n, Node parent) {
