"162 :   @Override
163 :   public void addMapping(
164 :       String sourceName, @Nullable String symbolName,
165 :       FilePosition sourceStartPosition,
166 :       FilePosition startPosition, FilePosition endPosition) {
167 : 
168 :     // Don't bother if there is not sufficient information to be useful.
169 :     if (sourceName == null || sourceStartPosition.getLine() < 0) {
170 :       return;
171 :     }
172 : 
173 :     FilePosition adjustedStart = startPosition;
174 :     FilePosition adjustedEnd = endPosition;
175 : 
176 :     if (offsetPosition.getLine() != 0
177 :         || offsetPosition.getColumn() != 0) {
178 :       // If the mapping is found on the first line, we need to offset
179 :       // its character position by the number of characters found on
180 :       // the *last* line of the source file to which the code is
181 :       // being generated.
182 :       int offsetLine = offsetPosition.getLine();
183 :       int startOffsetPosition = offsetPosition.getColumn();
184 :       int endOffsetPosition = offsetPosition.getColumn();
185 : 
186 :       if (startPosition.getLine() > 0) {
187 :         startOffsetPosition = 0;
188 :       }
189 : 
190 :       if (endPosition.getLine() > 0) {
191 :         endOffsetPosition = 0;
192 :       }
193 : 
194 :       adjustedStart = new FilePosition(
195 :           startPosition.getLine() + offsetLine,
196 :           startPosition.getColumn() + startOffsetPosition);
197 : 
198 :       adjustedEnd = new FilePosition(
199 :           endPosition.getLine() + offsetLine,
200 :           endPosition.getColumn() + endOffsetPosition);
201 :     }
202 : 
203 :     // Create the new mapping.
204 :     Mapping mapping = new Mapping();
205 :     mapping.sourceFile = sourceName;
206 :     mapping.originalPosition = sourceStartPosition;
207 :     mapping.originalName = symbolName;
208 :     mapping.startPosition = adjustedStart;
209 :     mapping.endPosition = adjustedEnd;
210 : 
211 :     // Validate the mappings are in a proper order.
212 :     if (lastMapping != null) {
213 :       int lastLine = lastMapping.startPosition.getLine();
214 :       int lastColumn = lastMapping.startPosition.getColumn();
215 :       int nextLine = mapping.startPosition.getLine();
216 :       int nextColumn = mapping.startPosition.getColumn();
217 :       Preconditions.checkState(nextLine > lastLine
218 :           || (nextLine == lastLine && nextColumn >= lastColumn),
219 :           "Incorrect source mappings order, previous : (%s,%s)\n"
220 :           + "new : (%s,%s)\nnode : %s",
221 :           lastLine, lastColumn, nextLine, nextColumn);
222 :     }
223 : 
224 :     lastMapping = mapping;
225 :     mappings.add(mapping);
226 :   }"

