"257 :         private void guessAOmega() {
258 :             // initialize the sums for the linear model between the two integrals
259 :             double sx2 = 0;
260 :             double sy2 = 0;
261 :             double sxy = 0;
262 :             double sxz = 0;
263 :             double syz = 0;
264 : 
265 :             double currentX = observations[0].getX();
266 :             double currentY = observations[0].getY();
267 :             double f2Integral = 0;
268 :             double fPrime2Integral = 0;
269 :             final double startX = currentX;
270 :             for (int i = 1; i < observations.length; ++i) {
271 :                 // one step forward
272 :                 final double previousX = currentX;
273 :                 final double previousY = currentY;
274 :                 currentX = observations[i].getX();
275 :                 currentY = observations[i].getY();
276 : 
277 :                 // update the integrals of f<sup>2</sup> and f'<sup>2</sup>
278 :                 // considering a linear model for f (and therefore constant f')
279 :                 final double dx = currentX - previousX;
280 :                 final double dy = currentY - previousY;
281 :                 final double f2StepIntegral =
282 :                     dx * (previousY * previousY + previousY * currentY + currentY * currentY) / 3;
283 :                 final double fPrime2StepIntegral = dy * dy / dx;
284 : 
285 :                 final double x = currentX - startX;
286 :                 f2Integral += f2StepIntegral;
287 :                 fPrime2Integral += fPrime2StepIntegral;
288 : 
289 :                 sx2 += x * x;
290 :                 sy2 += f2Integral * f2Integral;
291 :                 sxy += x * f2Integral;
292 :                 sxz += x * fPrime2Integral;
293 :                 syz += f2Integral * fPrime2Integral;
294 :             }
295 : 
296 :             // compute the amplitude and pulsation coefficients
297 :             double c1 = sy2 * sxz - sxy * syz;
298 :             double c2 = sxy * sxz - sx2 * syz;
299 :             double c3 = sx2 * sy2 - sxy * sxy;
300 :             if ((c1 / c2 < 0) || (c2 / c3 < 0)) {
301 :                 final int last = observations.length - 1;
302 :                 // Range of the observations, assuming that the
303 :                 // observations are sorted.
304 :                 final double xRange = observations[last].getX() - observations[0].getX();
305 :                 if (xRange == 0) {
306 :                     throw new ZeroException();
307 :                 }
308 :                 omega = 2 * Math.PI / xRange;
309 : 
310 :                 double yMin = Double.POSITIVE_INFINITY;
311 :                 double yMax = Double.NEGATIVE_INFINITY;
312 :                 for (int i = 1; i < observations.length; ++i) {
313 :                     final double y = observations[i].getY();
314 :                     if (y < yMin) {
315 :                         yMin = y;
316 :                     }
317 :                     if (y > yMax) {
318 :                         yMax = y;
319 :                     }
320 :                 }
321 :                 a = 0.5 * (yMax - yMin);
322 :             } else {
323 :                     // In some ill-conditioned cases (cf. MATH-844), the guesser
324 :                     // procedure cannot produce sensible results.
325 : 
326 :                 a = FastMath.sqrt(c1 / c2);
327 :                 omega = FastMath.sqrt(c2 / c3);
328 :             }
329 :         }"

