"173 :   CanInlineResult canInlineReferenceToFunction(NodeTraversal t,
174 :       Node callNode, Node fnNode, Set<String> needAliases,
175 :       InliningMode mode, boolean referencesThis, boolean containsFunctions) {
176 :     // TODO(johnlenz): This function takes too many parameter, without
177 :     // context.  Modify the API to take a structure describing the function.
178 : 
179 :     // Allow direct function calls or "fn.call" style calls.
180 :     if (!isSupportedCallType(callNode)) {
181 :       return CanInlineResult.NO;
182 :     }
183 : 
184 :     // Limit where functions that contain functions can be inline.  Introducing
185 :     // an inner function into another function can capture a variable and cause
186 :     // a memory leak.  This isn't a problem in the global scope as those values
187 :     // last until explicitly cleared.
188 :     if (containsFunctions) {
189 :       if (!assumeMinimumCapture && !t.inGlobalScope()) {
190 :         // TODO(johnlenz): Allow inlining into any scope without local names or
191 :         // inner functions.
192 :         return CanInlineResult.NO;
193 :       } else if (NodeUtil.isWithinLoop(callNode)) {
194 :         // An inner closure maybe relying on a local value holding a value for a
195 :         // single iteration through a loop.
196 :         return CanInlineResult.NO;
197 :       }
198 :     }
199 : 
200 :     // TODO(johnlenz): Add support for 'apply'
201 :     if (referencesThis && !NodeUtil.isFunctionObjectCall(callNode)) {
202 :       // TODO(johnlenz): Allow 'this' references to be replaced with a
203 :       // global 'this' object.
204 :       return CanInlineResult.NO;
205 :     }
206 : 
207 :     if (mode == InliningMode.DIRECT) {
208 :       return canInlineReferenceDirectly(callNode, fnNode);
209 :     } else {
210 :       return canInlineReferenceAsStatementBlock(
211 :           t, callNode, fnNode, needAliases);
212 :     }
213 :   }"

