"540 :     public XYDataItem addOrUpdate(Number x, Number y) {
541 :         if (x == null) {
542 :             throw new IllegalArgumentException("Null 'x' argument.");
543 :         }
544 : 
545 :         // if we get to here, we know that duplicate X values are not permitted
546 :         XYDataItem overwritten = null;
547 :         int index = indexOf(x);
548 :         if (index >= 0 && !this.allowDuplicateXValues) {
549 :             XYDataItem existing = (XYDataItem) this.data.get(index);
550 :             try {
551 :                 overwritten = (XYDataItem) existing.clone();
552 :             }
553 :             catch (CloneNotSupportedException e) {
554 :                 throw new SeriesException("Couldn't clone XYDataItem!");
555 :             }
556 :             existing.setY(y);
557 :         }
558 :         else {
559 :             // if the series is sorted, the negative index is a result from
560 :             // Collections.binarySearch() and tells us where to insert the
561 :             // new item...otherwise it will be just -1 and we should just
562 :             // append the value to the list...
563 :             if (this.autoSort) {
564 :                 this.data.add(-index - 1, new XYDataItem(x, y));
565 :             }
566 :             else {
567 :                 this.data.add(new XYDataItem(x, y));
568 :             }
569 :             // check if this addition will exceed the maximum item count...
570 :             if (getItemCount() > this.maximumItemCount) {
571 :                 this.data.remove(0);
572 :             }
573 :         }
574 :         fireSeriesChanged();
575 :         return overwritten;
576 :     }"

