"131 :   @Override
132 :   public void hotSwapScript(Node root, Node originalRoot) {
133 :     Traversal traversal = new Traversal();
134 :     NodeTraversal.traverse(compiler, root, traversal);
135 : 
136 :     if (!traversal.hasErrors()) {
137 : 
138 :       // Apply the aliases.
139 :       List<AliasUsage> aliasWorkQueue =
140 :           Lists.newArrayList(traversal.getAliasUsages());
141 :       while (!aliasWorkQueue.isEmpty()) {
142 :         List<AliasUsage> newQueue = Lists.newArrayList();
143 :         for (AliasUsage aliasUsage : aliasWorkQueue) {
144 :           if (aliasUsage.referencesOtherAlias()) {
145 :             newQueue.add(aliasUsage);
146 :           } else {
147 :             aliasUsage.applyAlias();
148 :           }
149 :         }
150 : 
151 :         // Prevent an infinite loop.
152 :         if (newQueue.size() == aliasWorkQueue.size()) {
153 :           Var cycleVar = newQueue.get(0).aliasVar;
154 :           compiler.report(JSError.make(
155 :               cycleVar.getNode(), GOOG_SCOPE_ALIAS_CYCLE, cycleVar.getName()));
156 :           break;
157 :         } else {
158 :           aliasWorkQueue = newQueue;
159 :         }
160 :       }
161 : 
162 :       // Remove the alias definitions.
163 :       for (Node aliasDefinition : traversal.getAliasDefinitionsInOrder()) {
164 :         if (aliasDefinition.getParent().isVar() &&
165 :             aliasDefinition.getParent().hasOneChild()) {
166 :           aliasDefinition.getParent().detachFromParent();
167 :         } else {
168 :           aliasDefinition.detachFromParent();
169 :         }
170 :       }
171 : 
172 :       // Collapse the scopes.
173 :       for (Node scopeCall : traversal.getScopeCalls()) {
174 :         Node expressionWithScopeCall = scopeCall.getParent();
175 :         Node scopeClosureBlock = scopeCall.getLastChild().getLastChild();
176 :         scopeClosureBlock.detachFromParent();
177 :         expressionWithScopeCall.getParent().replaceChild(
178 :             expressionWithScopeCall,
179 :             scopeClosureBlock);
180 :         NodeUtil.tryMergeBlock(scopeClosureBlock);
181 :       }
182 : 
183 :       if (traversal.getAliasUsages().size() > 0 ||
184 :           traversal.getAliasDefinitionsInOrder().size() > 0 ||
185 :           traversal.getScopeCalls().size() > 0) {
186 :         compiler.reportCodeChange();
187 :       }
188 :     }
189 :   }"

