"609 :   private static String toSource(Node root, Format outputFormat,
610 :                                  boolean lineBreak,  int lineLengthThreshold,
611 :                                  SourceMap sourceMap,
612 :                                  SourceMap.DetailLevel sourceMapDetailLevel,
613 :                                  Charset outputCharset,
614 :                                  boolean tagAsStrict) {
615 :     Preconditions.checkState(sourceMapDetailLevel != null);
616 : 
617 :     boolean createSourceMap = (sourceMap != null);
618 :     MappedCodePrinter mcp =
619 :         outputFormat == Format.COMPACT
620 :         ? new CompactCodePrinter(
621 :             lineBreak, lineLengthThreshold,
622 :             createSourceMap, sourceMapDetailLevel)
623 :         : new PrettyCodePrinter(
624 :             lineLengthThreshold, createSourceMap, sourceMapDetailLevel);
625 :     CodeGenerator cg =
626 :         outputFormat == Format.TYPED
627 :         ? new TypedCodeGenerator(mcp, outputCharset)
628 :         : new CodeGenerator(mcp, outputCharset);
629 : 
630 :     if (tagAsStrict) {
631 :       cg.tagAsStrict();
632 :     }
633 : 
634 :     cg.add(root);
635 :     mcp.endFile();
636 : 
637 :     String code = mcp.getCode();
638 : 
639 :     if (createSourceMap) {
640 :       mcp.generateSourceMap(sourceMap);
641 :     }
642 : 
643 :     return code;
644 :   }"

