" 62 :     public RectangularCholeskyDecomposition(RealMatrix matrix, double small)
 63 :         throws NonPositiveDefiniteMatrixException {
 64 : 
 65 :         final int order = matrix.getRowDimension();
 66 :         final double[][] c = matrix.getData();
 67 :         final double[][] b = new double[order][order];
 68 : 
 69 :         int[] swap  = new int[order];
 70 :         int[] index = new int[order];
 71 :         for (int i = 0; i < order; ++i) {
 72 :             index[i] = i;
 73 :         }
 74 : 
 75 :         int r = 0;
 76 :         for (boolean loop = true; loop;) {
 77 : 
 78 :             // find maximal diagonal element
 79 :             swap[r] = r;
 80 :             for (int i = r + 1; i < order; ++i) {
 81 :                 int ii  = index[i];
 82 :                 int isi = index[swap[i]];
 83 :                 if (c[ii][ii] > c[isi][isi]) {
 84 :                     swap[r] = i;
 85 :                 }
 86 :             }
 87 : 
 88 : 
 89 :             // swap elements
 90 :             if (swap[r] != r) {
 91 :                 int tmp = index[r];
 92 :                 index[r] = index[swap[r]];
 93 :                 index[swap[r]] = tmp;
 94 :             }
 95 : 
 96 :             // check diagonal element
 97 :             int ir = index[r];
 98 :             if (c[ir][ir] < small) {
 99 : 
100 :                 if (r == 0) {
101 :                     throw new NonPositiveDefiniteMatrixException(c[ir][ir], ir, small);
102 :                 }
103 : 
104 :                 // check remaining diagonal elements
105 :                 for (int i = r; i < order; ++i) {
106 :                     if (c[index[i]][index[i]] < -small) {
107 :                         // there is at least one sufficiently negative diagonal element,
108 :                         // the symmetric positive semidefinite matrix is wrong
109 :                         throw new NonPositiveDefiniteMatrixException(c[index[i]][index[i]], i, small);
110 :                     }
111 :                 }
112 : 
113 :                 // all remaining diagonal elements are close to zero, we consider we have
114 :                 // found the rank of the symmetric positive semidefinite matrix
115 :                 ++r;
116 :                 loop = false;
117 : 
118 :             } else {
119 : 
120 :                 // transform the matrix
121 :                 final double sqrt = FastMath.sqrt(c[ir][ir]);
122 :                 b[r][r] = sqrt;
123 :                 final double inverse  = 1 / sqrt;
124 :                 for (int i = r + 1; i < order; ++i) {
125 :                     final int ii = index[i];
126 :                     final double e = inverse * c[ii][ir];
127 :                     b[i][r] = e;
128 :                     c[ii][ii] -= e * e;
129 :                     for (int j = r + 1; j < i; ++j) {
130 :                         final int ij = index[j];
131 :                         final double f = c[ii][ij] - e * b[j][r];
132 :                         c[ii][ij] = f;
133 :                         c[ij][ii] = f;
134 :                     }
135 :                 }
136 : 
137 :                 // prepare next iteration
138 :                 loop = ++r < order;
139 :             }
140 :         }
141 : 
142 :         // build the root matrix
143 :         rank = r;
144 :         root = MatrixUtils.createRealMatrix(order, r);
145 :         for (int i = 0; i < order; ++i) {
146 :             for (int j = 0; j < r; ++j) {
147 :                 root.setEntry(index[i], j, b[i][j]);
148 :             }
149 :         }
150 : 
151 :     }"

