"611 :     private void findEigenvalues()
612 :         throws InvalidMatrixException {
613 : 
614 :         // compute splitting points
615 :         List<Integer> splitIndices = computeSplits();
616 : 
617 :         // find realEigenvalues in each block
618 :         realEigenvalues = new double[main.length];
619 :         imagEigenvalues = new double[main.length];
620 :         int begin = 0;
621 :         for (final int end : splitIndices) {
622 :             final int n = end - begin;
623 :             switch (n) {
624 : 
625 :             case 1:
626 :                 // apply dedicated method for dimension 1
627 :                 process1RowBlock(begin);
628 :                 break;
629 : 
630 :             case 2:
631 :                 // apply dedicated method for dimension 2
632 :                 process2RowsBlock(begin);
633 :                 break;
634 : 
635 :             case 3:
636 :                 // apply dedicated method for dimension 3
637 :                 process3RowsBlock(begin);
638 :                 break;
639 : 
640 :             default:
641 : 
642 :                 // choose an initial shift for LDL<sup>T</sup> decomposition
643 :                 final double[] range       = eigenvaluesRange(begin, n);
644 :                 final double oneFourth     = 0.25 * (3 * range[0] + range[1]);
645 :                 final int oneFourthCount   = countEigenValues(oneFourth, begin, n);
646 :                 final double threeFourth   = 0.25 * (range[0] + 3 * range[1]);
647 :                 final int threeFourthCount = countEigenValues(threeFourth, begin, n);
648 :                 final boolean chooseLeft   = (oneFourthCount - 1) >= (n - threeFourthCount);
649 :                 final double lambda        = chooseLeft ? range[0] : range[1];
650 : 
651 :                 tau = (range[1] - range[0]) * MathUtils.EPSILON * n + 2 * minPivot;
652 : 
653 :                 // decompose T-&lambda;I as LDL<sup>T</sup>
654 :                 ldlTDecomposition(lambda, begin, n);
655 : 
656 :                 // apply general dqd/dqds method
657 :                 processGeneralBlock(n);
658 : 
659 :                 // extract realEigenvalues
660 :                 if (chooseLeft) {
661 :                     for (int i = 0; i < n; ++i) {
662 :                         realEigenvalues[begin + i] = lambda + work[4 * i];
663 :                     }
664 :                 } else {
665 :                     for (int i = 0; i < n; ++i) {
666 :                         realEigenvalues[begin + i] = lambda - work[4 * i];
667 :                     }
668 :                 }
669 : 
670 :             }
671 :             begin = end;
672 :         }
673 : 
674 :         // sort the realEigenvalues in decreasing order
675 :         Arrays.sort(realEigenvalues);
676 :         int j = realEigenvalues.length - 1;
677 :         for (int i = 0; i < j; ++i) {
678 :             final double tmp = realEigenvalues[i];
679 :             realEigenvalues[i] = realEigenvalues[j];
680 :             realEigenvalues[j] = tmp;
681 :             --j;
682 :         }
