"272 :     public static DateTimeZone forOffsetHoursMinutes(int hoursOffset, int minutesOffset) throws IllegalArgumentException {
273 :         if (hoursOffset == 0 && minutesOffset == 0) {
274 :             return DateTimeZone.UTC;
275 :         }
276 :         if (hoursOffset < -23 || hoursOffset > 23) {
277 :             throw new IllegalArgumentException("Hours out of range: " + hoursOffset);
278 :         }
279 :         if (minutesOffset < 0 || minutesOffset > 59) {
280 :             throw new IllegalArgumentException("Minutes out of range: " + minutesOffset);
281 :         }
282 :         int offset = 0;
283 :         try {
284 :             int hoursInMinutes = hoursOffset * 60;
285 :             if (hoursInMinutes < 0) {
286 :                 minutesOffset = hoursInMinutes - minutesOffset;
287 :             } else {
288 :                 minutesOffset = hoursInMinutes + minutesOffset;
289 :             }
290 :             offset = FieldUtils.safeMultiply(minutesOffset, DateTimeConstants.MILLIS_PER_MINUTE);
291 :         } catch (ArithmeticException ex) {
292 :             throw new IllegalArgumentException("Offset is too large");
293 :         }
294 :         return forOffsetMillis(offset);
295 :     }"

