"178 :   private void computeGenKill(Node n, BitSet gen, BitSet kill,
179 :       boolean conditional) {
180 : 
181 :     switch (n.getType()) {
182 :       case Token.SCRIPT:
183 :       case Token.BLOCK:
184 :       case Token.FUNCTION:
185 :         return;
186 : 
187 :       case Token.WHILE:
188 :       case Token.DO:
189 :       case Token.IF:
190 :         computeGenKill(NodeUtil.getConditionExpression(n), gen, kill,
191 :             conditional);
192 :         return;
193 : 
194 :       case Token.FOR:
195 :         if (!NodeUtil.isForIn(n)) {
196 :           computeGenKill(NodeUtil.getConditionExpression(n), gen, kill,
197 :               conditional);
198 :         } else {
199 :           // for(x in y) {...}
200 :           Node lhs = n.getFirstChild();
201 :           Node rhs = lhs.getNext();
202 :           if (NodeUtil.isVar(lhs)) {
203 :             // for(var x in y) {...}
204 :             lhs = lhs.getLastChild();
205 :           }
206 :             addToSetIfLocal(lhs, kill);
207 :             addToSetIfLocal(lhs, gen);
208 :           computeGenKill(rhs, gen, kill, conditional);
209 :         }
210 :         return;
211 : 
212 :       case Token.VAR:
213 :         for (Node c = n.getFirstChild(); c != null; c = c.getNext()) {
214 :           if (c.hasChildren()) {
215 :             computeGenKill(c.getFirstChild(), gen, kill, conditional);
216 :             if (!conditional) {
217 :               addToSetIfLocal(c, kill);
218 :             }
219 :           }
220 :         }
221 :         return;
222 : 
223 :       case Token.AND:
224 :       case Token.OR:
225 :         computeGenKill(n.getFirstChild(), gen, kill, conditional);
226 :         // May short circuit.
227 :         computeGenKill(n.getLastChild(), gen, kill, true);
228 :         return;
229 : 
230 :       case Token.HOOK:
231 :         computeGenKill(n.getFirstChild(), gen, kill, conditional);
232 :         // Assume both sides are conditional.
233 :         computeGenKill(n.getFirstChild().getNext(), gen, kill, true);
234 :         computeGenKill(n.getLastChild(), gen, kill, true);
235 :         return;
236 : 
237 :       case Token.NAME:
238 :         if (isArgumentsName(n)) {
239 :           markAllParametersEscaped();
240 :         } else {
241 :           addToSetIfLocal(n, gen);
242 :         }
243 :         return;
244 : 
245 :       default:
246 :         if (NodeUtil.isAssignmentOp(n) && NodeUtil.isName(n.getFirstChild())) {
247 :           Node lhs = n.getFirstChild();
248 :           if (!conditional) {
249 :             addToSetIfLocal(lhs, kill);
250 :           }
251 :           if (!NodeUtil.isAssign(n)) {
252 :             // assignments such as a += 1 reads a.
253 :             addToSetIfLocal(lhs, gen);
254 :           }
255 :           computeGenKill(lhs.getNext(), gen, kill, conditional);
256 :         } else {
257 :           for (Node c = n.getFirstChild(); c != null; c = c.getNext()) {
258 :             computeGenKill(c, gen, kill, conditional);
259 :           }
260 :         }
261 :         return;
262 :     }
263 :   }"

"265 :   private void addToSetIfLocal(Node node, BitSet set) {
266 :     Preconditions.checkState(NodeUtil.isName(node));
267 :     String name = node.getString();
268 :     if (!jsScope.isDeclared(name, false)) {
269 :       return;
270 :     }
271 :     Var var = jsScope.getVar(name);
272 :     if (!escaped.contains(var)) {
273 :       set.set(var.index);
274 :     }
275 :   }"

