"700 :     public int parseInto(ReadWritableInstant instant, String text, int position) {
701 :         DateTimeParser parser = requireParser();
702 :         if (instant == null) {
703 :             throw new IllegalArgumentException("Instant must not be null");
704 :         }
705 :         
706 :         long instantMillis = instant.getMillis();
707 :         Chronology chrono = instant.getChronology();
708 :         long instantLocal = instantMillis + chrono.getZone().getOffset(instantMillis);
709 :         chrono = selectChronology(chrono);
710 :         int defaultYear = chrono.year().get(instantLocal);
711 :         
712 :         DateTimeParserBucket bucket = new DateTimeParserBucket(
713 :             instantLocal, chrono, iLocale, iPivotYear, defaultYear);
714 :         int newPos = parser.parseInto(bucket, text, position);
715 :         instant.setMillis(bucket.computeMillis(false, text));
716 :         if (iOffsetParsed && bucket.getOffsetInteger() != null) {
717 :             int parsedOffset = bucket.getOffsetInteger();
718 :             DateTimeZone parsedZone = DateTimeZone.forOffsetMillis(parsedOffset);
719 :             chrono = chrono.withZone(parsedZone);
720 :         } else if (bucket.getZone() != null) {
721 :             chrono = chrono.withZone(bucket.getZone());
722 :         }
723 :         instant.setChronology(chrono);
724 :         if (iZone != null) {
725 :             instant.setZone(iZone);
726 :         }
727 :         return newPos;
728 :     }"

