"144 :     public void add(BoxAndWhiskerItem item, Comparable rowKey, 
145 :             Comparable columnKey) {
146 : 
147 :         this.data.addObject(item, rowKey, columnKey);
148 :         
149 :         // update cached min and max values
150 :         int r = this.data.getRowIndex(rowKey);
151 :         int c = this.data.getColumnIndex(columnKey);
152 :         if ((this.maximumRangeValueRow == r && this.maximumRangeValueColumn 
153 :                 == c) || (this.minimumRangeValueRow == r 
154 :                 && this.minimumRangeValueColumn == c))  {
155 :             updateBounds();
156 :         }
157 :         
158 :             double minval = Double.NaN;
159 :             if (item.getMinOutlier() != null) {
160 :                 minval = item.getMinOutlier().doubleValue();
161 :             }
162 :             double maxval = Double.NaN;
163 :             if (item.getMaxOutlier() != null) {
164 :                 maxval = item.getMaxOutlier().doubleValue();
165 :             }
166 :         
167 :             if (Double.isNaN(this.maximumRangeValue)) {
168 :                 this.maximumRangeValue = maxval;
169 :                 this.maximumRangeValueRow = r;
170 :                 this.maximumRangeValueColumn = c;
171 :             }
172 :             else if (maxval > this.maximumRangeValue) {
173 :                 this.maximumRangeValue = maxval;
174 :                 this.maximumRangeValueRow = r;
175 :                 this.maximumRangeValueColumn = c;
176 :             }
177 :         
178 :             if (Double.isNaN(this.minimumRangeValue)) {
179 :                 this.minimumRangeValue = minval;
180 :                 this.minimumRangeValueRow = r;
181 :                 this.minimumRangeValueColumn = c;
182 :             }
183 :             else if (minval < this.minimumRangeValue) {
184 :                 this.minimumRangeValue = minval;
185 :                 this.minimumRangeValueRow = r;
186 :                 this.minimumRangeValueColumn = c;
187 :             }
188 :         
189 :         this.rangeBounds = new Range(this.minimumRangeValue,
190 :               this.maximumRangeValue);
191 :         fireDatasetChanged();
192 : 
193 :     }"

