"199 :   private void removeDeadExprStatementSafely(Node n) {
200 :     Node parent = n.getParent();
201 :     if (n.getType() == Token.EMPTY ||
202 :         (n.getType() == Token.BLOCK && !n.hasChildren())) {
203 :       // Not always trivial to remove, let FoldContants work its magic later.
204 :       return;
205 :     }
206 : 
207 :     switch (n.getType()) {
208 :       // Removing an unreachable DO node is messy because it means we still have
209 :       // to execute one iteration. If the DO's body has breaks in the middle, it
210 :       // can get even more trickier and code size might actually increase.
211 :       case Token.DO:
212 :         return;
213 : 
214 :       case Token.BLOCK:
215 :         // BLOCKs are used in several ways including wrapping CATCH blocks in TRYs
216 :         if (parent.getType() == Token.TRY) {
217 :           if (NodeUtil.isTryCatchNodeContainer(n)) {
218 :             return;
219 :           }
220 :         }
221 :         break;
222 : 
223 :       case Token.CATCH:
224 :         Node tryNode = parent.getParent();
225 :         NodeUtil.maybeAddFinally(tryNode);
226 :         break;
227 :     }
228 : 
229 :     NodeUtil.redeclareVarsInsideBranch(n);
230 :     compiler.reportCodeChange();
231 :     if (logger.isLoggable(Level.FINE)) {
232 :       logger.fine("Removing " + n.toString());
233 :     }
234 :     NodeUtil.removeChild(n.getParent(), n);
235 :   }"

