102 :     }
103 :     if (gNode.getAnnotation() != GraphReachability.REACHABLE ||
104 :         (removeNoOpStatements && !NodeUtil.mayHaveSideEffects(n))) {
105 :       removeDeadExprStatementSafely(n);
106 :       return;
107 :     }
108 : 
109 :     tryRemoveUnconditionalBranching(n);
110 :   }"

