259 :             // return isZero ? NaN : INF; // See MATH-657
260 :             return isZero ? NaN : INF;
261 :         }
262 : 
263 :         if (divisor.isInfinite() && !isInfinite()) {
264 :             return ZERO;
265 :         }
266 : 
267 :         final double c = divisor.getReal();
268 :         final double d = divisor.getImaginary();
269 : 
270 :         if (FastMath.abs(c) < FastMath.abs(d)) {
271 :             double q = c / d;
272 :             double denominator = c * q + d;
273 :             return createComplex((real * q + imaginary) / denominator,
274 :                 (imaginary * q - real) / denominator);
275 :         } else {
276 :             double q = d / c;
277 :             double denominator = d * q + c;
278 :             return createComplex((imaginary * q + real) / denominator,
279 :                 (imaginary - real * q) / denominator);
280 :         }
281 :     }"

