"313 :   public Rotation(Vector3D u1, Vector3D u2, Vector3D v1, Vector3D v2) {
314 : 
315 :   // norms computation
316 :   double u1u1 = u1.getNormSq();
317 :   double u2u2 = u2.getNormSq();
318 :   double v1v1 = v1.getNormSq();
319 :   double v2v2 = v2.getNormSq();
320 :   if ((u1u1 == 0) || (u2u2 == 0) || (v1v1 == 0) || (v2v2 == 0)) {
321 :     throw MathRuntimeException.createIllegalArgumentException(LocalizedFormats.ZERO_NORM_FOR_ROTATION_DEFINING_VECTOR);
322 :   }
323 : 
324 :   // normalize v1 in order to have (v1'|v1') = (u1|u1)
325 :   v1 = new Vector3D(FastMath.sqrt(u1u1 / v1v1), v1);
326 : 
327 :   // adjust v2 in order to have (u1|u2) = (v1'|v2') and (v2'|v2') = (u2|u2)
328 :   double u1u2   = u1.dotProduct(u2);
329 :   double v1v2   = v1.dotProduct(v2);
330 :   double coeffU = u1u2 / u1u1;
331 :   double coeffV = v1v2 / u1u1;
332 :   double beta   = FastMath.sqrt((u2u2 - u1u2 * coeffU) / (v2v2 - v1v2 * coeffV));
333 :   double alpha  = coeffU - beta * coeffV;
334 :   v2 = new Vector3D(alpha, v1, beta, v2);
335 : 
336 :   // preliminary computation
337 :   Vector3D uRef  = u1;
338 :   Vector3D vRef  = v1;
339 :   Vector3D v1Su1 = v1.subtract(u1);
340 :   Vector3D v2Su2 = v2.subtract(u2);
341 :   Vector3D k     = v1Su1.crossProduct(v2Su2);
342 :   Vector3D u3    = u1.crossProduct(u2);
343 :   double c       = k.dotProduct(u3);
344 :   if (c == 0) {
345 :     // the (q1, q2, q3) vector is close to the (u1, u2) plane
346 :     // we try other vectors
347 :     Vector3D v3 = Vector3D.crossProduct(v1, v2);
348 :     Vector3D v3Su3 = v3.subtract(u3);
349 :     k = v1Su1.crossProduct(v3Su3);
350 :     Vector3D u2Prime = u1.crossProduct(u3);
351 :     c = k.dotProduct(u2Prime);
352 : 
353 :     if (c == 0) {
354 :       // the (q1, q2, q3) vector is also close to the (u1, u3) plane,
355 :       // it is almost aligned with u1: we try (u2, u3) and (v2, v3)
356 :       k = v2Su2.crossProduct(v3Su3);;
357 :       c = k.dotProduct(u2.crossProduct(u3));;
358 : 
359 :       if (c == 0) {
360 :         // the (q1, q2, q3) vector is aligned with everything
361 :         // this is really the identity rotation
362 :         q0 = 1.0;
363 :         q1 = 0.0;
364 :         q2 = 0.0;
365 :         q3 = 0.0;
366 :         return;
367 :       }
368 : 
369 :       // we will have to use u2 and v2 to compute the scalar part
370 :       uRef = u2;
371 :       vRef = v2;
372 : 
373 :     }
374 : 
375 :   }
376 : 
377 :   // compute the vectorial part
378 :   c = FastMath.sqrt(c);
379 :   double inv = 1.0 / (c + c);
380 :   q1 = inv * k.getX();
381 :   q2 = inv * k.getY();
382 :   q3 = inv * k.getZ();
383 : 
384 :   // compute the scalar part
385 :    k = new Vector3D(uRef.getY() * q3 - uRef.getZ() * q2,
386 :                     uRef.getZ() * q1 - uRef.getX() * q3,
387 :                     uRef.getX() * q2 - uRef.getY() * q1);
388 :   q0 = vRef.dotProduct(k) / (2 * k.getNormSq());
389 : 
390 :   }"

