"107 :   public static ParseResult parse(StaticSourceFile sourceFile,
108 :                                   String sourceString,
109 :                                   Config config,
110 :                                   ErrorReporter errorReporter,
111 :                                   Logger logger) throws IOException {
112 :     Context cx = Context.enter();
113 :     cx.setErrorReporter(errorReporter);
114 :     cx.setLanguageVersion(Context.VERSION_1_5);
115 :     CompilerEnvirons compilerEnv = new CompilerEnvirons();
116 :     compilerEnv.initFromContext(cx);
117 :     compilerEnv.setRecordingComments(true);
118 :     compilerEnv.setRecordingLocalJsDocComments(true);
119 : 
120 :     // ES5 specifically allows trailing commas
121 :     compilerEnv.setWarnTrailingComma(
122 :         config.languageMode == LanguageMode.ECMASCRIPT3);
123 : 
124 :     compilerEnv.setReservedKeywordAsIdentifier(true);
125 : 
126 :     compilerEnv.setAllowMemberExprAsFunctionName(false);
127 :     compilerEnv.setIdeMode(config.isIdeMode);
128 :     compilerEnv.setRecoverFromErrors(config.isIdeMode);
129 : 
130 :     Parser p = new Parser(compilerEnv, errorReporter);
131 :     AstRoot astRoot = null;
132 :     try {
133 :       astRoot = p.parse(sourceString, sourceFile.getName(), 1);
134 :     } catch (EvaluatorException e) {
135 :       logger.info(
136 :           "Error parsing " + sourceFile.getName() + ": " + e.getMessage());
137 :     } finally {
138 :       Context.exit();
139 :     }
140 :     Node root = null;
141 :     if (astRoot != null) {
142 :       root = IRFactory.transformTree(
143 :           astRoot, sourceFile, sourceString, config, errorReporter);
144 :       root.setIsSyntheticBlock(true);
145 :     }
146 :     return new ParseResult(root, astRoot);
147 :   }"

