"108 :   private boolean collapseAssignEqualTo(Node expr, Node exprParent,
109 :       Node value) {
110 :     Node assign = expr.getFirstChild();
111 :     Node parent = exprParent;
112 :     Node next = expr.getNext();
113 :     while (next != null) {
114 :       switch (next.getType()) {
115 :         case Token.AND:
116 :         case Token.OR:
117 :         case Token.HOOK:
118 :         case Token.IF:
119 :         case Token.RETURN:
120 :         case Token.EXPR_RESULT:
121 :           // Dive down the left side
122 :           parent = next;
123 :           next = next.getFirstChild();
124 :           break;
125 : 
126 :         case Token.VAR:
127 :           if (next.getFirstChild().hasChildren()) {
128 :             parent = next.getFirstChild();
129 :             next = parent.getFirstChild();
130 :             break;
131 :           }
132 :           return false;
133 : 
134 :         case Token.GETPROP:
135 :         case Token.NAME:
136 :           if (next.isQualifiedName()) {
137 :             String nextName = next.getQualifiedName();
138 :             if (value.isQualifiedName() &&
139 :                 nextName.equals(value.getQualifiedName())) {
140 :               // If the previous expression evaluates to value of a
141 :               // qualified name, and that qualified name is used again
142 :               // shortly, then we can exploit the assign here.
143 : 
144 :               // Verify the assignment doesn't change its own value.
145 :               if (!isSafeReplacement(next, assign)) {
146 :                 return false;
147 :               }
148 : 
149 :               exprParent.removeChild(expr);
150 :               expr.removeChild(assign);
151 :               parent.replaceChild(next, assign);
152 :               return true;
153 :             }
154 :           }
155 :           return false;
156 : 
157 :         case Token.ASSIGN:
158 :           // Assigns are really tricky. In lots of cases, we want to inline
159 :           // into the right side of the assign. But the left side of the
160 :           // assign is evaluated first, and it may have convoluted logic:
161 :           //   a = null;
162 :           //   (a = b).c = null;
163 :           // We don't want to exploit the first assign. Similarly:
164 :           //   a.b = null;
165 :           //   a.b.c = null;
166 :           // We don't want to exploit the first assign either.
167 :           //
168 :           // To protect against this, we simply only inline when the left side
169 :           // is guaranteed to evaluate to the same L-value no matter what.
170 :           Node leftSide = next.getFirstChild();
171 :           if (leftSide.isName() ||
172 :               leftSide.isGetProp() &&
173 :               leftSide.getFirstChild().isThis()) {
174 :             // Dive down the right side of the assign.
175 :             parent = next;
176 :             next = leftSide.getNext();
177 :             break;
178 :           } else {
179 :             return false;
180 :           }
181 : 
182 :         default:
183 :           if (NodeUtil.isImmutableValue(next)
184 :               && next.isEquivalentTo(value)) {
185 :             // If the r-value of the expr assign is an immutable value,
186 :             // and the value is used again shortly, then we can exploit
187 :             // the assign here.
188 :             exprParent.removeChild(expr);
189 :             expr.removeChild(assign);
190 :             parent.replaceChild(next, assign);
191 :             return true;
192 :           }
193 :           // Return without inlining a thing
194 :           return false;
195 :       }
196 :     }
197 : 
198 :     return false;
199 :   }"

"206 :   private boolean isSafeReplacement(Node node, Node replacement) {
207 :     // No checks are needed for simple names.
208 :     if (node.isName()) {
209 :       return true;
210 :     }
211 :     Preconditions.checkArgument(node.isGetProp());
212 : 
213 :       node = node.getFirstChild();
214 :     if (node.isName()
215 :         && isNameAssignedTo(node.getString(), replacement)) {
216 :       return false;
217 :     }
218 : 
219 :     return true;
220 :   }"

