"228 :     public void visit(NodeTraversal t, Node n, Node parent) {
229 :       if (n.getType() == Token.NAME) {
230 :         switch (parent.getType()) {
231 :           case Token.VAR:
232 :           case Token.FUNCTION:
233 :           case Token.LP:
234 :             // These are okay.
235 :             break;
236 :           case Token.GETPROP:
237 :             if (n == parent.getFirstChild()) {
238 :               Scope scope = t.getScope();
239 :               Scope.Var var = scope.getVar(n.getString());
240 :               if (var == null) {
241 :                 t.report(n, UNDEFINED_EXTERN_VAR_ERROR, n.getString());
242 :                 varsToDeclareInExterns.add(n.getString());
243 :               }
244 :             }
245 :             break;
246 :           default:
247 :             t.report(n, NAME_REFERENCE_IN_EXTERNS_ERROR, n.getString());
248 : 
249 :             Scope scope = t.getScope();
250 :             Scope.Var var = scope.getVar(n.getString());
251 :             if (var == null) {
252 :               varsToDeclareInExterns.add(n.getString());
253 :             }
254 :             break;
255 :         }
256 :       }
257 :     }"

