"34 :     @Override
35 :     public int translate(CharSequence input, int index, Writer out) throws IOException {
36 :         // TODO: Protect from ArrayIndexOutOfBounds
37 :         if(input.charAt(index) == '&' && input.charAt(index + 1) == '#') {
38 :             int start = index + 2;
39 :             boolean isHex = false;
40 : 
41 :             char firstChar = input.charAt(start);
42 :             if(firstChar == 'x' || firstChar == 'X') {
43 :                 start++;
44 :                 isHex = true;
45 :             }
46 : 
47 :             int end = start;
48 :             while(input.charAt(end) != ';') {
49 :                 end++;
50 :             }
51 : 
52 :             int entityValue;
53 :             try {
54 :                 if(isHex) {
55 :                     entityValue = Integer.parseInt(input.subSequence(start, end).toString(), 16);
56 :                 } else {
57 :                     entityValue = Integer.parseInt(input.subSequence(start, end).toString(), 10);
58 :                 }
59 :             } catch(NumberFormatException nfe) {
60 :                 return 0;
61 :             }
62 : 
63 :                 out.write(entityValue);
64 :             return 2 + (end - start) + (isHex ? 1 : 0) + 1;
65 :         }
66 :         return 0;
67 :     }"

