"367 :     @Override
368 :     protected PointValuePair doOptimize() {
369 :          // -------------------- Initialization --------------------------------
370 :         isMinimize = getGoalType().equals(GoalType.MINIMIZE);
371 :         final FitnessFunction fitfun = new FitnessFunction();
372 :         final double[] guess = getStartPoint();
373 :         // number of objective variables/problem dimension
374 :         dimension = guess.length;
375 :         initializeCMA(guess);
376 :         iterations = 0;
377 :         double bestValue = fitfun.value(guess);
378 :         push(fitnessHistory, bestValue);
379 :         PointValuePair optimum
380 :             = new PointValuePair(getStartPoint(),
381 :                                  isMinimize ? bestValue : -bestValue);
382 :         PointValuePair lastResult = null;
383 : 
384 :         // -------------------- Generation Loop --------------------------------
385 : 
386 :         generationLoop:
387 :         for (iterations = 1; iterations <= maxIterations; iterations++) {
388 : 
389 :             // Generate and evaluate lambda offspring
390 :             final RealMatrix arz = randn1(dimension, lambda);
391 :             final RealMatrix arx = zeros(dimension, lambda);
392 :             final double[] fitness = new double[lambda];
393 :             // generate random offspring
394 :             for (int k = 0; k < lambda; k++) {
395 :                 RealMatrix arxk = null;
396 :                 for (int i = 0; i < checkFeasableCount + 1; i++) {
397 :                     if (diagonalOnly <= 0) {
398 :                         arxk = xmean.add(BD.multiply(arz.getColumnMatrix(k))
399 :                                          .scalarMultiply(sigma)); // m + sig * Normal(0,C)
400 :                     } else {
401 :                         arxk = xmean.add(times(diagD,arz.getColumnMatrix(k))
402 :                                          .scalarMultiply(sigma));
403 :                     }
404 :                     if (i >= checkFeasableCount ||
405 :                         fitfun.isFeasible(arxk.getColumn(0))) {
406 :                         break;
407 :                     }
408 :                     // regenerate random arguments for row
409 :                     arz.setColumn(k, randn(dimension));
410 :                 }
411 :                 copyColumn(arxk, 0, arx, k);
412 :                 try {
413 :                     fitness[k] = fitfun.value(arx.getColumn(k)); // compute fitness
414 :                 } catch (TooManyEvaluationsException e) {
415 :                     break generationLoop;
416 :                 }
417 :             }
418 :             // Sort by fitness and compute weighted mean into xmean
419 :             final int[] arindex = sortedIndices(fitness);
420 :             // Calculate new xmean, this is selection and recombination
421 :             final RealMatrix xold = xmean; // for speed up of Eq. (2) and (3)
422 :             final RealMatrix bestArx = selectColumns(arx, MathArrays.copyOf(arindex, mu));
423 :             xmean = bestArx.multiply(weights);
424 :             final RealMatrix bestArz = selectColumns(arz, MathArrays.copyOf(arindex, mu));
425 :             final RealMatrix zmean = bestArz.multiply(weights);
426 :             final boolean hsig = updateEvolutionPaths(zmean, xold);
427 :             if (diagonalOnly <= 0) {
428 :                 updateCovariance(hsig, bestArx, arz, arindex, xold);
429 :             } else {
430 :                 updateCovarianceDiagonalOnly(hsig, bestArz);
431 :             }
432 :             // Adapt step size sigma - Eq. (5)
433 :             sigma *= Math.exp(Math.min(1, (normps/chiN - 1) * cs / damps));
434 :             final double bestFitness = fitness[arindex[0]];
435 :             final double worstFitness = fitness[arindex[arindex.length - 1]];
436 :             if (bestValue > bestFitness) {
437 :                 bestValue = bestFitness;
438 :                 lastResult = optimum;
439 :                 optimum = new PointValuePair(fitfun.repair(bestArx.getColumn(0)),
440 :                                              isMinimize ? bestFitness : -bestFitness);
441 :                 if (getConvergenceChecker() != null &&
442 :                     lastResult != null) {
443 :                     if (getConvergenceChecker().converged(iterations, optimum, lastResult)) {
444 :                         break generationLoop;
445 :                     }
446 :                 }
447 :             }
448 :             // handle termination criteria
449 :             // Break, if fitness is good enough
450 :             if (stopFitness != 0) { // only if stopFitness is defined
451 :                 if (bestFitness < (isMinimize ? stopFitness : -stopFitness)) {
452 :                     break generationLoop;
453 :                 }
454 :             }
455 :             final double[] sqrtDiagC = sqrt(diagC).getColumn(0);
456 :             final double[] pcCol = pc.getColumn(0);
457 :             for (int i = 0; i < dimension; i++) {
458 :                 if (sigma * Math.max(Math.abs(pcCol[i]), sqrtDiagC[i]) > stopTolX) {
459 :                     break;
460 :                 }
461 :                 if (i >= dimension - 1) {
462 :                     break generationLoop;
463 :                 }
464 :             }
465 :             for (int i = 0; i < dimension; i++) {
466 :                 if (sigma * sqrtDiagC[i] > stopTolUpX) {
467 :                     break generationLoop;
468 :                 }
469 :             }
470 :             final double historyBest = min(fitnessHistory);
471 :             final double historyWorst = max(fitnessHistory);
472 :             if (iterations > 2 &&
473 :                 Math.max(historyWorst, worstFitness) -
474 :                 Math.min(historyBest, bestFitness) < stopTolFun) {
475 :                 break generationLoop;
476 :             }
477 :             if (iterations > fitnessHistory.length &&
478 :                 historyWorst - historyBest < stopTolHistFun) {
479 :                 break generationLoop;
480 :             }
481 :             // condition number of the covariance matrix exceeds 1e14
482 :             if (max(diagD) / min(diagD) > 1e7) {
483 :                 break generationLoop;
484 :             }
485 :             // user defined termination
486 :             if (getConvergenceChecker() != null) {
487 :                 final PointValuePair current
488 :                     = new PointValuePair(bestArx.getColumn(0),
489 :                                          isMinimize ? bestFitness : -bestFitness);
490 :                 if (lastResult != null &&
491 :                     getConvergenceChecker().converged(iterations, current, lastResult)) {
492 :                     break generationLoop;
493 :                     }
494 :                 lastResult = current;
495 :             }
496 :             // Adjust step size in case of equal function values (flat fitness)
497 :             if (bestValue == fitness[arindex[(int)(0.1+lambda/4.)]]) {
498 :                 sigma = sigma * Math.exp(0.2 + cs / damps);
499 :             }
500 :             if (iterations > 2 && Math.max(historyWorst, bestFitness) -
501 :                 Math.min(historyBest, bestFitness) == 0) {
502 :                 sigma = sigma * Math.exp(0.2 + cs / damps);
503 :             }
504 :             // store best in history
505 :             push(fitnessHistory,bestFitness);
506 :             fitfun.setValueRange(worstFitness-bestFitness);
507 :             if (generateStatistics) {
508 :                 statisticsSigmaHistory.add(sigma);
509 :                 statisticsFitnessHistory.add(bestFitness);
510 :                 statisticsMeanHistory.add(xmean.transpose());
511 :                 statisticsDHistory.add(diagD.transpose().scalarMultiply(1E5));
512 :             }
513 :         }
514 :         return optimum;
515 :     }"

